UE.registerUI("button",function(e,n){e.registerCommand(n,{execCommand:function(){alert("execCommand:"+n)}});var t=new UE.ui.Button({name:n,title:n,cssRules:"background-position: -500px 0;",onclick:function(){e.execCommand(n)}});return e.addListener("selectionchange",function(){var o=e.queryCommandState(n);-1==o?(t.setDisabled(!0),t.setChecked(!1)):(t.setDisabled(!1),t.setChecked(o))}),t});